mapper.select:
SELECT c.* FROM cashregister c,member m WHERE c.memberID=m.memberID
AND (m.name LIKE "%"#{PhoneOrName}"%" OR m.phone LIKE "%"#{PhoneOrName}"%")
AND c.time >= #{startTime}
AND c.time <= #{endTime}
ORDER BY c.time DESC

SELECT * FROM cashregister b LEFT JOIN member d ON b.memberID=d.memberID LEFT JOIN shop c ON b.shopID=c.shopID where 1=1 and b.ispresent=1
and b.shopid = #{shopid}
and b.time like '%${selectqb}%' OR b.ashRegisterID like '%${selectqb}%' OR c.shopname like '%${selectqb}%' OR d.name like '%${selectqb}%' OR b.cashregistername like '%${selectqb}%' OR d.price like '%${selectqb}%'
AND b.time > #{oldtime}
AND b.time < #{newtime}
AND YEAR(b.time)= #{ddnf}

select year(a.time) 'ddnf',month(a.time) 'ddyf',sum(a.moneyamt) 'ddsumprice',count(a.moneyamt) 'ddcount' ,c.* from cashregister a LEFT JOIN shop c ON a.shopID=c.shopID where 1=1 and a.ispresent=1
and c.shopid = #{shopid}
AND YEAR(a.time)= #{ddnf}
group by year(a.time),month(a.time)

     SELECT * FROM cashregister b 
	LEFT JOIN member d ON b.memberID=d.memberID 
	LEFT JOIN shop c ON b.shopID=c.shopID 
    where 1=1
    and b.ispresent=1

          and b.shopid = #{shopid}


          and c.shopname like '%${selectqb}%'


		AND b.time > #{oldtime}


		AND b.time < #{newtime}

	group by c.shopid

SELECT *,f.name as spname,SUM(a.count) as spsccount ,SUM(a.moneyamt) as spsccprice FROM cashregisterdetail a INNER JOIN cashregister b ON a.cashRegisterID=b.ashRegisterID LEFT JOIN CommodityDetail e ON e.commodityDetailID=a.commodityDetailID LEFT JOIN Commodity f ON f.productCodeID=e. productCodeID LEFT JOIN member d ON b.memberID=d.memberID LEFT JOIN shop c ON b.shopID=c.shopID LEFT JOIN color g ON g.colorID=e.colorID LEFT JOIN commoditysize h ON h.commoditySizeID=e.commoditySizeID where 1=1 and b.ispresent=1
and b.shopid = #{shopid}
and f.commoditytypeid = #{commoditytypeid}
and f.name like '%${selectqb}%' OR e.commodityDetailID like '%${selectqb}%' OR c.shopname like '%${selectqb}%' OR g.color like '%${selectqb}%' OR h.commoditysize like '%${selectqb}%' OR f.price like '%${selectqb}%' OR e.count like '%${selectqb}%'
AND b.time > #{oldtime}
AND b.time < #{newtime}
group by a.commodityDetailID
