mapper.select:
SELECT * FROM commodity a INNER JOIN supplier b ON a.supplierID=b.supplierID INNER JOIN commoditytype c ON a.commodityTypeID=c.commodityTypeID INNER JOIN commoditydetail d ON a.productCodeID=d.productCodeID INNER JOIN bill e ON e.supplierID=b.supplierID INNER JOIN users h ON h.userID=e.userID INNER JOIN detail f ON f.billID=e.billID INNER JOIN commoditydetail g ON g.commodityDetailID=f.commodityDetailID where 1=1
and e.shopid like '%${shopid}%'
and e.billid like '%${selectqb}%' OR e.deliverytime like '%${selectqb}%'
AND e.deliveryTime > #{oldtime}
AND e.deliveryTime < #{newtime}
GROUP BY e.billID

mapper.insert:
insert into commodity (productCodeID, supplierID, brand, 
  name, price, costPrice, commodityTypeID, 
  commodityImg, stockCount, infomation, 
  shopType, shopID, spxj
  )
values (#{productcodeid,jdbcType=VARCHAR}, #{supplierid,jdbcType=INTEGER}, #{brand,jdbcType=VARCHAR}, 
  #{name,jdbcType=VARCHAR}, #{price,jdbcType=REAL}, #{costprice,jdbcType=REAL}, #{commoditytypeid,jdbcType=INTEGER}, 
  #{commodityimg,jdbcType=VARCHAR}, #{stockcount,jdbcType=INTEGER}, #{infomation,jdbcType=VARCHAR}, 
  #{shoptype,jdbcType=VARCHAR}, #{shopid,jdbcType=VARCHAR}, #{spxj,jdbcType=INTEGER}
  )
